import React from 'react';
import {
  Button,
  Col,
  Container,
  Row,
  Image,
} from 'react-bootstrap';

import front1 from "../assets/front1.jpg";
import front2 from "../assets/front2.jpg";
import front3 from "../assets/front3.jpg";
import front4 from "../assets/front4.jpg";
import front5 from "../assets/front5.jpg";
import front6 from "../assets/front6.jpg";

import Slider from 'react-slick';

const settings = {
  dots: false,
  arrows: false,
  fade: true,
  infinite: true,
  autoplay: true,
  speed: 1000,
  autoplaySpeed: 2000,
  slidesToShow: 1,
  slidesToScroll: 1,
};

export default function FrontCarousel() {
  const [slider, setSlider] = React.useState(null);

  const top = '50%';
  const side = '40px';

  const cards = [
    {
      title: "SHAPE YOUR BODY",
      text: "TRAINING HARD",
      be: "BE",
      strong: "POWER",
      image: front2,
    },
    {
      title: "SHAPE YOUR BODY",
      text: "NO GAIN",
      be: "NO",
      strong: "PAIN",
      image: front3,
    },
    {
      title: "SHAPE YOUR BODY",
      text: "TRAINING HARD",
      be: "BE",
      strong: "HERO",
      image: front4,
    },
    {
      title: "SHAPE YOUR BODY",
      text: "TRAINING HARD",
      be: "BE",
      strong: "MIGHTY",
      image: front1,
    },
    {
      title: "SHAPE YOUR BODY",
      text: "TRAINING HARD",
      be: "BE",
      strong: "STRONG",
      image: front5,
    },
    {
      title: "SHAPE YOUR BODY",
      text: "WORK INSANE",
      be: "BE",
      strong: "FLAME",
      image: front6,
    },
  ];

  return (
    <Container
      className="position-relative"
      style={{ height: '100vh', width: '100vh', overflow: 'hidden' }}
    >
      <Slider {...settings} ref={(slider) => setSlider(slider)}>
        {cards.map((card, index) => (
          <div
            key={index}
            style={{
              height: '6xl',
              position: 'relative',
              backgroundPosition: 'center',
              backgroundRepeat: 'no-repeat',
              backgroundSize: 'cover',
              backgroundPositionX: 'fixed',
              backgroundAttachment: 'fixed',
              backgroundImage: `url(${card.image})`,
            }}
          >
            <Container
              className="position-relative"
              style={{ height: '600px' }}
            >
              <Row className="position-absolute top-50 start-50 translate-middle">
                <Col className="text-center">
                  <h3>{card.title}</h3>
                  <h1>
                    {card.be}
                    <span style={{ color: '#f45f02' }}> {card.strong}</span>
                  </h1>
                  <p>{card.text}</p>
                  <Button variant="primary">GET INFO</Button>
                </Col>
              </Row>
            </Container>
          </div>
        ))}
      </Slider>
      <Button
        className="position-absolute"
        variant="light"
        style={{ left: side, top: top, transform: 'translate(0%, -50%)' }}
        onClick={() => slider?.slickPrev()}
      >
        
      </Button>
      <Button
        className="position-absolute"
        variant="light"
        style={{ right: side, top: top, transform: 'translate(0%, -50%)' }}
        onClick={() => slider?.slickNext()}
      >
        
      </Button>
    </Container>
  );
}
