import React from 'react';
import { Box, Container, Row, Col, Image, Button } from 'react-bootstrap';
import { Link, useParams } from 'react-router-dom';
import workout from '../assets/workout.png';
import { FaRocket, FaCoffee, FaNutritionix } from 'react-icons/fa';

const SinglePlanPage = () => {
  const { plan } = useParams();
  let data = [
    // Your data array here
  ];

  return (
    <Box bgGradient="linear-gradient(180deg, rgba(0,0,0,1) 20%, rgba(64,64,64,1) 93%)" w="100%">
      <Container className="py-5">
        <Row className="justify-content-center">
          <Col xs={12} className="text-center">
            <h1 className="text-white">{plan}</h1>
            <h2 className="text-white">WORKOUT SCHEDULE</h2>
            <p className="text-white">
              Lorem ipsum dolor sit amet, consectetur adipisicing elit, sed do eiusmod tempor incididunt. Excepteur
              sint occaecat cupidatat non proident, sunt in culpa qui officia deserunt.
            </p>
            <Link to={`/plans/${plan}/workout`}>
              <Image src={workout} className="w-50" />
            </Link>
            <h2 className="text-white">THIS WORKOUT PLAN INCLUDES</h2>
          </Col>
        </Row>
        <Row className="justify-content-center">
          {data?.map((item, index) => (
            <Col key={index} xs={12} md={4} className="mb-4">
              <Box className="p-4 bg-white rounded shadow">
                <Box className="text-center">
                  <item.logo size={40} className="text-white" />
                </Box>
                <h3 className="text-center text-white">{item.name}</h3>
                <p className="text-center text-white">{item.type}</p>
                <p className="text-center text-white">{item.desc}</p>
                <div className="text-center">
                  <Link to={`/plans/${item.name}`}>
                    <Button variant="primary">{item.button}</Button>
                  </Link>
                </div>
              </Box>
            </Col>
          ))}
        </Row>
      </Container>
    </Box>
  );
};

export default SinglePlanPage;
