import React from "react";
import { Breadcrumb, Container, Row, Col, Card, Button } from "react-bootstrap";
import { Link } from "react-router-dom";
import banner from "../assets/img1.png";
import wave from "../assets/wave2.png";


const Plans = () => {
  let data = [
    // Your data array here
  ];

  return (
    <div className="plans-container">
      <div
        className="banner"
        style={{
          backgroundImage: `url(${banner})`
        }}
      >
        <img src={wave} alt="wave" className="wave" />
        <Container>
          <h1 className="banner-heading">Plans</h1>
          <Breadcrumb>
            <Breadcrumb.Item>
              <Link to="/">Home</Link>
            </Breadcrumb.Item>
            <Breadcrumb.Item active>Plans</Breadcrumb.Item>
          </Breadcrumb>
        </Container>
      </div>
      <Container>
        <Row xs={1} md={2} lg={3} className="g-4 mt-5">
          {data.map((item, index) => (
            <Col key={index}>
              <Card className="h-100">
                <Card.Img variant="top" src={item.image} />
                <Card.Body>
                  <Card.Title>{item.name}</Card.Title>
                  <Card.Text>{item.desc}</Card.Text>
                </Card.Body>
                <Card.Footer>
                  <small className="text-muted">
                    Type: {item.type} | Gender: {item.gender} | Duration:{" "}
                    {item.duration}
                  </small>
                </Card.Footer>
                <div className="d-grid">
                  <Link to={`/plans/${item.name}`}>
                    <Button variant="primary">VIEW DETAILS</Button>
                  </Link>
                </div>
              </Card>
            </Col>
          ))}
        </Row>
      </Container>
    </div>
  );
};

export default Plans;
